function optLine()

a = rand(3,1);
b= rand(3,1);
SkewSymMat(a) * SkewSymMat(b) - SkewSymMat(b) * SkewSymMat(a) - SkewSymMat(SkewSymMat(a) * b)


p1 = [100 200 300]';
p2 = [103 180 150]';
p1 = 10 * rand(3,1);
p2 = 10 * rand(3,1);
p1 = sign(p1(3)) * p1;
p2 = sign(p2(3)) * p2;

dir = (p2 - p1)./norm(p2 - p1);


px = p1 + 10 * dir;

dir1 = p1./norm(p1);
dir2 = p2./norm(p2);
rou1 = 1/ norm(p1);
rou2 = 1/ norm(p2);

dir_x = px./norm(px);

[roux, xyz, d_roux_d_rou12] = computeJac(dir1, dir2, rou1,rou2, dir_x);
[roux_1, xyz_1, d_roux_d_rou12_1] = computeJac(dir1, dir2, rou1,rou2, dir1);
[roux_2, xyz_2, d_roux_d_rou12_2] = computeJac(dir1, dir2, rou1,rou2, dir2);

[[roux_1 - rou1] [roux_2 - rou2] [xyz_1 - p1]' [xyz_2 - p2]']

[[xyz - px]' [roux - 1/norm(px)]]




dir_x_stack = [];
obs_rou_x_stack = [];
for i = 1 : 20
    px_temp = p1 + 10 * i * dir;
    dir_x_temp = px_temp./norm(px_temp);
    dir_x_stack(:,i) = dir_x_temp;
    obs_rou_x_stack(:,i) = 1/norm(px_temp);
end



rou1_gt = rou1;
rou2_gt = rou2;

rou1 = rou1_gt + 2;
rou2 = rou2_gt + 1;

dx_sum = zeros(2,1);
for iter = 1 : 20
    
    
    H = zeros(2,2);
    b = zeros(2,1);
    err_sum = 0;
    for i = 1 : length(obs_rou_x_stack)
        [roux, px, d_roux_d_rou12] = computeJac(dir1, dir2, rou1, rou2, dir_x_stack(:,i));
        [roux_2, px_2, d_roux_d_rou12_2] = computeJac(dir2, dir1, rou2, rou1, dir_x_stack(:,i));
        check = d_roux_d_rou12([2 1]) - d_roux_d_rou12_2;
        err = roux - obs_rou_x_stack(1, i);
        H = H + d_roux_d_rou12' * d_roux_d_rou12;
        b = b - d_roux_d_rou12' * err;
        err_sum = err_sum + err.^2;
    end
    
    dx = inv(H) * b;
    dx_sum = dx_sum + dx;
    fprintf(sprintf('iter: %d, err: %f£¬ dx: [%f %f], dx_sum: [%f %f], rou12_diff: [%f %f]\n', iter, err_sum, dx(1), dx(2), dx_sum(1), dx_sum(2), rou1 - rou1_gt, rou2 - rou2_gt));
    
    rou1 = rou1 + dx(1);
    rou2 = rou2 + dx(2);
    rou1 = sign(rou1) * rou1;
    rou2 = sign(rou2) * rou2;
    
end



end
